import sysimport osfrom BitOfNews import Summarize as BON_Summarizefrom TextRank import Summarize as TextRank_Summarizefrom Modified_TextRank import Summarize as MTextRank_Summarizedef MTextRank_Summary(files):	for k in files:		modified_output = (k[0].replace('DUC', 'MOD'), k[1].replace('DUC', 'MOD'))		try:			os.makedirs(modified_output[0])		except:			pass		MTextRank_Summarize(k[1], modified_output[1])def TextRank_Summary(files):	for k in files:		modified_output = (k[0].replace('DUC', 'TRS'), k[1].replace('DUC', 'TRS'))		try:			os.makedirs(modified_output[0])		except:			pass		TextRank_Summarize(k[1], modified_output[1])		def BON_Summary(files):	for k in files:		modified_output = (k[0].replace('DUC', 'BON'), k[1].replace('DUC', 'BON'))		try:			os.makedirs(modified_output[0])		except:			pass		BON_Summarize(k[1], modified_output[1])def OPT_Summary(files):	for k in files:		modified_output = (k[0].replace('DUC', 'OPT'), k[1].replace('DUC', 'OPT'))		try:			os.makedirs(modified_output[0])		except:			passif __name__ == '__main__':	options = sys.argv[1]		files = []	path = './DUC/2002/data/test/docs.with.sentence.breaks/'	for folder in os.listdir(path):		if '.tar' in folder: continue		tmp_path = path + folder		for article in os.listdir(tmp_path):			files.append((tmp_path, tmp_path+'/'+article))	if 'M' in options:		MTextRank_Summary(files)	if 'T' in options:		TextRank_Summary(files)	if 'B' in options:		BON_Summary(files)